import React from 'react'
import { useParams } from 'react-router-dom'
import posts from 'json/posts.json'

function Post() {

    const params  = useParams()

    const post = posts.find((post) => {return post.id === Number(params.id)})



    return (
        <div>
            <h1>Posts {params.id}</h1>
            <p>{post.texto}</p>
        </div>
    )
}

export default Post
